mapper.select:
SELECT inner_table.*
FROM( SELECT USER_ID, USER_PASSWORD, USER_NAME, USER_PHONE, USER_BIRTH, USER_ADDR, USER_ADDR_DETAIL, USER_MAP_LA, USER_MAP_LO, REPORT_COUNT,
             USER_EMAIL, USER_EMAIL_STATUS, USER_PRO_IMG, USER_FAV_MENU, USER_FAV_PLACE, USER_FAV_PLACE_DETAIL, USER_INTRO, USER_TOTAL_POINT, USER_REG_DATE
	  FROM user




					AND user_id LIKE #{searchKeyword}


					AND user_name LIKE #{searchKeyword}



	  order by user_id ) inner_table
limit 0,8;

SELECT inner_table.*
FROM( SELECT USER_ID, USER_PASSWORD, USER_NAME, USER_PHONE, REPORT_COUNT, USER_EMAIL, USER_EMAIL_STATUS, ROLE, BLACK_REG_DATE, BLACK_END_DATE
FROM user




			AND user_id LIKE #{searchKeyword}


			AND user_name LIKE #{searchKeyword}



order by user_id ) inner_table
limit 0,8;

SELECT COUNT(*)
FROM(	SELECT USER_ID, USER_NAME, USER_PHONE, USER_BIRTH, USER_ADDR, USER_ADDR_DETAIL, USER_MAP_LA, USER_MAP_LO, REPORT_COUNT, USER_EMAIL, USER_EMAIL_STATUS, USER_REG_DATE
FROM user



			user_id = #{searchKeyword}


			user_name = #{searchKeyword}


 ) countTable

mapper.update:
UPDATE user
user_name = #{userName}, user_phone = #{userPhone}, user_birth = #{userBirth}, user_addr = #{userAddr}, user_addr_detail = #{userAddrDetail}, user_email = #{userEmail}, user_email_status = #{userEmailStatus}
WHERE user_id = #{userId}

UPDATE user
user_pro_img = #{userProImg}, user_fav_menu = #{userFavMenu}, user_fav_place = #{userFavPlace}, user_fav_place_detail = #{userFavPlaceDetail}, user_fav_menu = #{userFavMenu}, user_intro = #{userIntro}
WHERE user_id = #{userId}

UPDATE user

	user_map_la		= #{userMapLa},
	user_map_lo		= #{userMapLo}

WHERE user_id = #{userId}

mapper.select:
SELECT user_id
FROM user
WHERE user_name = #{userName}  AND  role != '1'

		AND user_email = #{userEmail}


		AND user_phone = #{userPhone}

mapper.update:
UPDATE user
user_bye_reg_date = SYSDATE(), user_bye_reason = #{userByeReason}, user_bye_status = 1
WHERE user_id = #{userId}

UPDATE user
role = '2', black_reg_date = SYSDATE(), black_end_date = DATE_ADD(SYSDATE(), INTERVAL 3 DAY)
WHERE user_id = #{userId}
