mapper.update:
UPDATE menu
MENU_NAME = #{menuName}, MENU_PRICE = #{menuPrice}, MENU_DETAIL = #{menuDetail}, IS_SIG_MENU = #{isSigMenu}, MENU_IMG1 = #{menuImg1}, MENU_IMG2 = #{menuImg2}, MENU_IMG3 = #{menuImg3}
WHERE MENU_NO = #{menuNo}

UPDATE optiongroup

OPTION_GROUP_NAME = #{optionGroupName:VARCHAR},
OPTION_NAME = #{optionName:VARCHAR},
OPTION_PRICE = #{optionPrice:INTEGER}

WHERE OPTION_NO = #{optionNo}
