mapper.select:
select round(avg(RV_STAR),1) as RV_STAR
from review
where RV_TRUCK_ID=#{truckId}
group by RV_TRUCK_ID;

mapper.update:
UPDATE review
RV_STAR = #{rvStar}, RV_CONTENT = #{rvContent}, RV_IMG1=#{rvImg1}, RV_IMG2=#{rvImg2}, RV_IMG3=#{rvImg3}
WHERE RV_NO = #{rvNo}

UPDATE review
RV_TRUCK_CMT_CONTENT = #{rvTruckCmtContent}, RV_TRUCK_CMT_REG_TIME = SYSDATE()
WHERE RV_NO = #{rvNo}

mapper.select:
SELECT COUNT(*)
FROM(	SELECT *
FROM review
		 WHERE RV_TRUCK_ID=#{truckId}
	) countTable

SELECT COUNT(*)
FROM(	SELECT *
FROM review
		 WHERE RV_USER_ID=#{userId}
	) countTable

SELECT COUNT(*) as rv_no
FROM(	SELECT *
FROM review
		 WHERE RV_TRUCK_ID=#{truckId}
	) countTable
