mapper.select:
SELECT count(*)
FROM truck
WHERE truck_id = #{truckId}

SELECT count(*)
FROM truck
WHERE truck_name = #{truckName}

SELECT inner_table.* FROM( SELECT t.truck_id , t.truck_name , t.truck_phone, t.truck_map_la, t.truck_map_lo, t.truck_cate, t.truck_avg_star, t.truck_pro_img, t.truck_busi_status, t.truck_sig_menu_no, t.truck_sig_menu_name, t.truck_sig_menu_img1, m.menu_truck_id, m.menu_name, (6371*acos(cos(radians(37.4685225))*cos(radians(t.truck_map_la))*cos(radians(t.truck_map_lo) -radians(126.8943311))+sin(radians(37.4685225))*sin(radians(t.truck_map_la)))) as distance FROM truck t left join menu m on m.menu_truck_id=t.truck_id
AND ((t.truck_name LIKE #{searchKeyword} or t.truck_sig_menu_name like #{searchKeyword}) or m.menu_name like #{searchKeyword})
AND t.truck_cate = #{cateCondition}
group by truck_id
having distance <= 500
order by truck_id ) inner_table limit 0,20;

mapper.update:
UPDATE truck
truck_id = #{truckId}, truck_password = #{truckPassword}, truck_ceo_name= #{truckCEOName}, truck_ceo_birth = #{truckCEOBirth}, truck_addr = #{truckAddr}, truck_addr_detail = #{truckAddrDetail}, truck_phone = #{truckPhone}, truck_email = #{truckEmail}, truck_name = #{truckName}, truck_busi_lice = #{truckBusiLice}, truck_busi_no = #{truckBusiNo}, truck_cate = #{truckCate}, truck_pro_img = #{truckProImg}, truck_ceo_intro = #{truckCEOIntro}, truck_avg_star = #{truckAVGStar}, truck_map_la = #{truckMapLa}, truck_map_lo = #{truckMapLo}, truck_join_req_status = #{truckJoinReqStatus}, truck_join_req_date = #{truckJoinReqDate}, truck_notice_title = #{truckNoticeTitle}, truck_notice_content = #{truckNoticeContent}, truck_notice_img = #{truckNoticeImg}, truck_bye_status = #{truckByeStatus}, truck_bye_reason = #{truckByeReason}, truck_busi_status = #{truckBusiStatus}, truck_reg_date = #{truckRegDate}, role = #{role}
WHERE truck_id = #{truckId}

UPDATE truck

	TRUCK_NOTICE_TITLE = #{truckNoticeTitle},
	TRUCK_NOTICE_CONTENT = #{truckNoticeContent},
	TRUCK_NOTICE_IMG = #{truckNoticeImg}

WHERE truck_id = #{truckId}

