mapper.select:
SELECT COUNT(*)
FROM(	SELECT truck_name, truck_map_la, truck_map_lo, truck_cate, truck_avg_star, truck_busi_status
FROM truck




			AND truck_name LIKE #{searchKeyword}
			ORDER BY truck_avg_star DESC


			AND  LIKE #{searchKeyword}


 ) countTable
